mapper.select:
select yhxx.yhid as id, yhxx.nsrsbh, yhxx.yh, yhxx.mm, IF(yhxx.lrsj='0000-00-00 00:00:00','null',DATE_FORMAT (yhxx.lrsj,'%Y-%m-%d %H:%i:%S')) zcsj, yhxx.yhzt, yhxx.yhmc, yhxx.lxdh as sjh, yhxx.dz, yhxx.yx, zhxx.nsrmc, yhxx.lrr, yhxx.xgr, yhxx.ip, IF(zhxx.xgsj='0000-00-00 00:00:00','null',DATE_FORMAT (zhxx.xgsj,'%Y-%m-%d %H:%i:%S')) xgsj, IF(zhxx.yxq_q='0000-00-00 00:00:00','null',DATE_FORMAT (zhxx.yxq_q,'%Y-%m-%d %H:%i:%S')) yxq_q, IF(zhxx.yxq_z='0000-00-00 00:00:00','null',DATE_FORMAT (zhxx.yxq_z,'%Y-%m-%d %H:%i:%S')) yxq_z, IF(yhxx.zhdlsj='0000-00-00 00:00:00','null',DATE_FORMAT (yhxx.zhdlsj,'%Y-%m-%d %H:%i:%S')) as zhdlsj, yhxx.bz, zhxx.ssjtnsrsbh, zhxx.sfcp, zhxx.zhbs, js.sfjtjs, js.iskj from spu_t_zhxx zhxx RIGHT JOIN spu_t_yhxx yhxx on zhxx.nsrsbh=yhxx.nsrsbh LEFT JOIN spu_t_yhjs_gl yhjs on yhjs.yhid = yhxx.yhid LEFT JOIN spu_t_js js on js.jsid = yhjs.jsid where 1=1 and zhxx.zhzt='0' and yhxx.yhzt='0'
and yhxx.yh=#{yhzh}
and yhxx.nsrsbh=#{nsrsbh}
and yhxx.mm=#{passWord}
and yhxx.yhid=#{id}

select t.cdid menu_code,t.cdmc menu_name,t.s_cdid praentId,t.cdxh menuOrder,t.cdjc,t.cd_url menu_src,t.cdtb menu_icon,t.cdgs from spu_t_cd_new t left join spu_t_jscd_gl a on t.cdid = a.cdid left join spu_t_yhjs_gl b on a.jsid = b.jsid left join spu_t_yhxx c on c.yhid = b.yhid where t.yxbz='Y' and a.yxbz = 'Y' and b.yxbz = 'Y' and c.yhzt ='0' and (t.cdjc = '3' or t.cdjc = '4')
and c.yhid=#{yhid}
and t.cdgs=#{cdid}
and a.yxq_q<=CURRENT_TIMESTAMP and a.yxq_z>=CURRENT_TIMESTAMP
order by praentId,t.cdxh

mapper.update:
		UPDATE  spu_t_yhxx     


			 lxdh=#{sjh},


		 	yhmc=#{yhmc},


			yx=#{yx}


        where yhid=#{id}

update spu_t_yhxx set 
mm = #{new_mm} 
where yhid = #{yhid}
